import streamlit as st
from io import BytesIO
from reportlab.pdfgen import canvas
from reportlab.lib.pagesizes import A4
from reportlab.lib.units import cm
from reportlab.lib.utils import ImageReader
import base64
from PIL import Image

logo_base64 = """"""

st.set_page_config(page_title="Presupuesto personalizado Innovation Crafters", layout="centered")
st.image("logo_innovation_crafters.png", width=200)
st.title("Presupuesto personalizado Innovation Crafters")

modalidad = st.selectbox("Modalidad", ["Standard", "Premium", "Platino"])

st.subheader("Indica cuÃ¡ntas bolsas quieres por cada formato:")
coste_bolsa_unitario = {
    "1g": 0.45, "2g": 0.45, "3g": 0.45,
    "5g": 0.50, "10g": 0.50, "20g": 0.50,
    "50g": 0.65, "100g": 0.65
}
mano_obra_precio = {"Standard": 300, "Premium": 275, "Platino": 300}
minimo_kg = {"Standard": 3.0, "Premium": 2.0, "Platino": 0.5}

bolsas = {}
total_gramos = 0
for formato in ['1g', '2g', '3g', '5g', '10g', '20g', '50g', '100g']:
    cantidad = st.number_input(f"Bolsas de {{formato}}", min_value=0, value=0, step=10)
    bolsas[formato] = cantidad
    gramos = int(formato.replace("g", "")) * cantidad
    total_gramos += gramos

total_kg = total_gramos / 1000
st.markdown(f"**Total: {total_gramos} gramos ({total_kg:.2f} kg)**")

if total_kg < minimo_kg[modalidad]:
    st.error(f"El mÃ­nimo para la modalidad '{modalidad}' es {minimo_kg[modalidad]} kg.")
    st.stop()

precio_mano_obra = mano_obra_precio[modalidad] * total_kg

# Calcular coste de bolsas si no es Premium
coste_total_bolsas = 0.0
if modalidad != "Premium":
    for formato, cantidad in bolsas.items():
        coste_total_bolsas += cantidad * coste_bolsa_unitario[formato]

tipo_envio = st.radio("DÃ­as de producciÃ³n estimada", ["Standard (3-5 dÃ­as hÃ¡biles + transporte)", "Express (3 dÃ­as hÃ¡biles + transporte)"])
recargo_eq = st.selectbox("Â¿Recargo de equivalencia (5,2%)?", ["No", "SÃ­"])
imagenes = st.file_uploader("Sube tus imÃ¡genes del producto", type=["png", "jpg", "jpeg"], accept_multiple_files=True)

envio = 50 * total_kg if "Express" in tipo_envio else 5
base = precio_mano_obra + coste_total_bolsas + envio
recargo = 0.052 * base if recargo_eq == "SÃ­" else 0
iva = 0.21 * (base + recargo)
total = base + recargo + iva

st.markdown(f"### Total presupuesto: {total:.2f} â‚¬ (IVA incluido)")

# Generar PDF
def generar_pdf():
    buffer = BytesIO()
    c = canvas.Canvas(buffer, pagesize=A4)
    width, height = A4

    try:
        logo = ImageReader(BytesIO(base64.b64decode(logo_base64)))
        c.drawImage(logo, 2 * cm, height - 3 * cm, width=4 * cm)
    except:
        pass

    y = height - 4.5 * cm
    c.setFont("Helvetica", 11)
    c.drawString(2 * cm, y, f"Modalidad: {modalidad}")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"Total kg: {total_kg:.2f} kg")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"Coste bolsas: {coste_total_bolsas:.2f} â‚¬")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"Coste mano de obra: {precio_mano_obra:.2f} â‚¬")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"EnvÃ­o: {envio:.2f} â‚¬")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"Recargo: {recargo:.2f} â‚¬")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"IVA: {iva:.2f} â‚¬")
    y -= 0.5 * cm
    c.drawString(2 * cm, y, f"TOTAL: {total:.2f} â‚¬")

    y -= 1 * cm
    c.setFont("Helvetica-Bold", 10)
    c.drawString(2 * cm, y, "ðŸ“Œ Detalle de bolsas:")
    y -= 0.5 * cm
    c.setFont("Helvetica", 10)
    for formato, cantidad in bolsas.items():
        if cantidad > 0:
            c.drawString(2.5 * cm, y, f"{{formato}}: {{cantidad}} bolsas")
            y -= 0.4 * cm

    y -= 0.5 * cm
    c.setFont("Helvetica", 10)
    for line in [
        "Inversiones Brandon e Hijos SL",
        "Calle Anselm ClavÃ© 7, 17300, Blanes, Gerona",
        "CIF: B42761262",
        "IBAN: ESXX XXXX XXXX XXXX XXXX XXXX",
        "Pago: 60% por adelantado, 40% al salir el producto"
    ]:
        c.drawString(2 * cm, y, line)
        y -= 0.4 * cm

    c.showPage()
    c.save()
    buffer.seek(0)
    return buffer

if st.button("ðŸ“„ Descargar presupuesto"):
    pdf = generar_pdf()
    st.download_button("Descargar PDF", data=pdf, file_name="presupuesto_cbd.pdf")